int check1(unsigned char* buf)
{
    int i, j;
    int count = 0;

    for(i = 0; i < 0x24; i++)
    {
        if(buf[i] == '-')
            continue;
        for(j = 0; j < 0x24; j++)
        {
            if(buf[j] == '-')
                continue;
            
            if(i == j)
                continue;

            if((buf[i]^buf[j]) != secret[count++])
                return 0;
        }
    }

    return 1;
}

int check2(unsigned char* buf)
{
    int i;
    unsigned int sum = 0;

    for(i = 0; i < 0x24; i++)
    {
        if(buf[i] == '-')
            continue;
        
        sum += buf[i];
    }

    if(sum != 2189)
        return 0;
    
    return 1;
}

int check(unsigned char* buf)
{
    int i;

    if(!check1(buf))
        return 0;

    if(!check2(buf))
        return 0;

    return 1;
}

void init()
{
	setvbuf(stdin,0,2,0);
	setvbuf(stdout,0,2,0);
	setvbuf(stderr,0,2,0);
}

