int check1(unsigned char* buf)
{
    int i, j;
    int count = 0;
    
    for(i = 1; i < 0x24; i++)
    {
        if(i == 8 || i == 13 || i == 18 || i ==23)
            continue;
        if((buf[0]^buf[i]) != secret[count++]){
            return 0;
		}
    }
	return 1;
}

int check2(unsigned char* buf)
{
    int i;
    unsigned int sum = 0;
    for(i = 0; i < 0x24; i++)
    {
        if(i == 8 || i == 13 || i ==18 || i == 23)
            continue;

        sum += buf[i];
    }
    if(sum != 2189)
        return 0;
    
    return 1;
}

int check(unsigned char* buf)
{
    int i;

    if(!check1(buf))
		return 0;

	if(!check2(buf))
        return 0;

    return 1;

}

